import React, { useEffect, useState, useRef } from 'react';
import "./App.css";

import * as faceapi from 'face-api.js';

export default function App() {
  const video = document.getElementById("video");
  const videoHeight = 720;
  const videoWidth = 1280;
  const [initializing, setInitializing] = useState(false)
  const videoRef = useRef();
  const canvasRef = useRef();

  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = process.env.PUBLIC_URL + '/models';
      setInitializing(true);
      Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        faceapi.nets.faceExpressionNet.loadFromUri(MODEL_URL)
      ]).then(startVideo())
    }
    loadModels();



    const startVideo = () => {
      navigator.getUserMedia = navigator.getUserMedia ||
        navigator.webkitGetUserMedia ||
        navigator.mozGetUserMedia;

      navigator.getUserMedia(
        {
          video: {},
        },
        function (stream) {
          video.srcObject = stream;
          video.play();
        },
        function (err) {
          console.log("hata: " + err.name);
        }
      );
    }


    videoRef.addEventListener("play", () => {
      const canvas = faceapi.createCanvasFromMedia(video);
      document.body.append(canvas);
      const boxSize = {
        width: video.width,
        heigth: video.height
      }

      faceapi.matchDimensions(canvas, boxSize);

      setInterval(async () => {
        if (initializing) {
          setInitializing(false);
        }

        const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions()).withFaceLandmarks().withFaceExpressions();
        canvas.getContext('2d').clearRect(0, 0, canvas.width, canvas.height);
        const resizedDetections = faceapi.resizeResults(detections, boxSize);
        faceapi.draw.drawDetections(canvas, resizedDetections);
        faceapi.draw.drawFaceLandmarks(canvas, resizedDetections);
        faceapi.draw.drawFaceExpressions(canvas, resizedDetections)
        //console.log(detections);
      }, 100)
    })

  });

  return (
    <div className="main">
      <span>{initializing ? "Initializing" : "Ready"}</span>
      <div>
        <video id="video" className="video" ref={videoRef} autoPlay muted height={videoHeight} width={videoWidth} />
        <canvas ref={canvasRef} />
      </div>
    </div>
  )
}
